// Access the transcription data from the previous node
const transcriptData = $input.first().json.word_details; // Adjust this based on your node's output structure

// Check if the data exists and is an array
if (!transcriptData || !Array.isArray(transcriptData)) {
  throw new Error("Transcription data is not an array or is undefined. Check the previous node's output.");
}

// Map the transcript data to match your previous format
const formattedTranscript = transcriptData.map((word, index) => {
  return {
    text: word.text,   // Adjust 'text' to match your data's property name (e.g., 'word' or 'text')
    index: index,      // Position in the array
    start: Math.floor(word.start/1000), // Start time (adjust units if needed, e.g., / 1000 for seconds)
    end: Math.ceil(word.end/1000)      // End time (adjust units if needed)
  };
});

// Convert the formatted transcript to a JSON string
const transcriptJson = JSON.stringify(formattedTranscript);

// Return the result in n8n‚Äôs expected format
return [{ json: { transcriptJson: transcriptJson } }];

