// Loop over input items and add a new field called 'myNewField' to the JSON of each one
let link = $('AI Agent').first().json.output[0].url.split("v=")[1]
return {link};

// Inputs you provide in n8n
const youtubeLink = $('If2').first().json.download_url; // e.g., "https://www.youtube.com/watch?v=abcd1234"
const gameplayLink = $('If3').first().json.download_url; // e.g., MP4 link or file URL

// const youtubeLink = "https://joan48.oceansaver.in/pacific/?W059PMLknMgPFrSp3RB0AYW"
// const gameplayLink = "https://patrick17.oceansaver.in/pacific/?f67T4SCEp5IrIHqzhqZPliJ"



// Optional: Clip settings
const youtubeClipStart = $('Loop Over Clips').first().json.start_second; // in seconds
const youtubeClipDuration = $('Loop Over Clips').first().json.end_second - youtubeClipStart; // in seconds

let clips = [];

// lets add subtitle
$('Loop Over Clips').first().json.transcript.forEach(seg => {
  clips.push({
    asset: {
      type: "html",
      "width": 1000,
      "height": 400,
      background: "transparent", // No background
      html: "<p data-html-type=\"text\">"+ seg.text + "</p>",
      "css": "p { font-size: 70px; font-family: 'Anton SC'; text-align: center; text-scale: shrink; }"
    },
    start: parseFloat((seg.start_second - youtubeClipStart).toFixed(2)),
    length: parseFloat((seg.end_second - seg.start_second).toFixed(2))
  });
});


// Build ShortStack JSON payload
const shortstackPayload = {
  "timeline": {
    "background": "#000000",
    "tracks": [
      // {
      //   "clips": clips
      // },
      {
        "clips": [
          {
            "length": "auto",
            "asset": {
              "type": "video",
              "src": gameplayLink,
              "volume": 0.1
            },
            "start": 0,
            "length": youtubeClipDuration,
            "offset": {
              "x": -0.02,
              "y": -0.235
            },
            "scale": 0.505,
            "position": "center"
          }
        ]
      },
      {
        "clips": [
          {
            "length": "auto",
            "asset": {
              "type": "video",
              "src": youtubeLink,
              "trim": youtubeClipStart
            },
            "start": 0,
            "length": youtubeClipDuration,
            "offset": {
              "x": -0.02,
              "y": 0.25
            },
            "scale": 0.465,
            "position": "center"
          }
        ]
      }
    ]
  },
  "output": {
    "format": "mp4",
    "fps": 25,
    "size": {
      "width": 1080,
      "height": 1920
    }
  },
  "callback": "https://magic.codesacure.com/webhook/bf61429a-999b-47fe-bc4d-be0ae42cc306"
}

// Return for n8n
return [
    {
        json: {
            shotstack_payload: shortstackPayload
        }
    }
];

function getRandomLink(links) {
    if (!Array.isArray(links) || links.length === 0) {
        return null; // No valid links
    }
    const randomIndex = Math.floor(Math.random() * links.length);
    return links[randomIndex];
}

